<script>
import ButtonVue from './Button.vue'
export default {
  computed: {
    /**
     * @description log in狀態 -> 若true，代表登入了
     */
    logInStatus() {
      if (this.userToken && this.userToken.length > 0) {
        return true
      } else {
        return false
      }
    },
    userImg() {
      return this.$store.state.globalPar.userImg
    },
  },
  props: {
    userToken: {
      type: String,
    },
  },
  data() {
    return {
      menuOpenStatus: false,
    }
  },
  components: {
    ButtonVue,
  },
  methods: {
    notDo() {},
    menuOpen(status) {
      this.menuOpenStatus = status
      this.$emit('menuOpenF', status)
    },
    /**
     * @description 開啟登入視窗
     */
    openLoginWindow() {
      this.$emit('openLoginWindow')
    },
  },
}
</script>





